        if (platformFee > 0) {
            usdc.transfer(feeWallet, platformFee);
        }

        // Transfer merchant amount to merchant
        if (merchantAmount > 0) {
            usdc.transfer(merchant, merchantAmount);
        }

        emit PaymentSplit(
